// Lấy vị trí top của phần tử cần bắt sự kiện
var elementPosition = 50;
var lienhe = 100;

// Bắt sự kiện cuộn trang
$(window).scroll(function() {
  // Lấy vị trí hiện tại của trang khi cuộn
  var currentScrollPosition = $(window).scrollTop();

  // So sánh vị trí hiện tại với vị trí của phần tử
  if (currentScrollPosition > elementPosition) {
    // Thêm class "scrolled" vào phần tử nếu vị trí hiện tại lớn hơn vị trí của phần tử
    $("#logo").addClass("resizeimg");
  } else {
    // Xóa class "scrolled" khỏi phần tử nếu vị trí hiện tại không lớn hơn vị trí của phần tử
    $("#logo").removeClass("resizeimg");
  }
});
